package hw1Q3JavaBasics;

public class My_info {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("My Name Is:"); // Single Line Comments
		System.out.println("---------------------------------"); // Creating seperation
		System.out.println("\"Manjur Ahmed Shuvha\""); // Use of \r and \" \"
		System.out.println("---------------------------------"); // Creating seperation
		System.out.println("\rMy Address Is:"); // jump to next line
		System.out.println("---------------------------------"); // Creating seperation
		System.out.println("14703, 88th Ave Apt B4"); // print line
		System.out.println("Jamiaca, NY 11435"); // next line
		System.out.println("---------------------------------"); // Creating seperation
	}

}
