package javaBasics;

public class HelloWorld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello World"); //Single Line Comments
		System.out.println("Can you hear me?"); //Single Line Comments with quotation characterized by \ command
		System.out.println("I am Donald \"Junkie\" Trump"); //Use of \r and \" \"
		System.out.print("Trump for 2020"); // print vs println
		System.out.println("Trump for 2020"); //print line merged with above
		System.out.println("\rTrump for Idoits"); //skip to next line
		System.out.println("M" + "A" + "G" + "A");
		System.out.println("M" + "A" + "G" + "A");
	// trying to push the code in github	
	}

}
